import { Col, Row, Input, Typography, Radio, Select, Tag } from "antd";
import { useState } from "react";
import { useDispatch } from "react-redux";
// import {
//   priorityFilterChange,
//   searchFilterChange,
//   statusFilterChange,
// } from "../../redux/actions";
import { filterSlice } from "./FilterSlice";
const { Search } = Input;

export default function Filters() {
  const [searchText, setSearchText] = useState("");
  const [statusValue, setstatusValue] = useState("All");
  const [filterPriority, setfilterPriority] = useState([]);

  const dispatch = useDispatch();

  const handleSearchFilter = (e) => {
    setSearchText(e.target.value);
    dispatch(filterSlice.actions.searchFilterChange(e.target.value));
    // dispatch(searchFilterChange(e.target.value));
  };
  const handlestatusValueChange = (e) => {
    setstatusValue(e.target.value);
    dispatch(filterSlice.actions.statusFilterChange(e.target.value));
    // dispatch(statusFilterChange(e.target.value));
  };

  const handlePriorityChange = (value) => {
    setfilterPriority(value);
    dispatch(filterSlice.actions.priorityFilterChange(value));
    // dispatch(priorityFilterChange(value));
  };

  return (
    <Row justify="center">
      <Col span={24}>
        <Typography.Paragraph
          style={{ fontWeight: "bold", marginBottom: 3, marginTop: 10 }}
        >
          Search
        </Typography.Paragraph>
        <Search
          placeholder="input search text"
          value={searchText}
          onChange={handleSearchFilter}
        />
      </Col>
      <Col sm={24}>
        <Typography.Paragraph
          style={{ fontWeight: "bold", marginBottom: 3, marginTop: 10 }}
        >
          Filter By Status
        </Typography.Paragraph>
        <Radio.Group value={statusValue} onChange={handlestatusValueChange}>
          <Radio value="All">All</Radio>
          <Radio value="Completed">Completed</Radio>
          <Radio value="Todo">To do</Radio>
        </Radio.Group>
      </Col>
      <Col sm={24}>
        <Typography.Paragraph
          style={{ fontWeight: "bold", marginBottom: 3, marginTop: 10 }}
        >
          Filter By Priority
        </Typography.Paragraph>
        <Select
          mode="multiple"
          allowClear
          placeholder="Please select"
          style={{ width: "100%" }}
          value={filterPriority}
          onChange={handlePriorityChange}
        >
          <Select.Option value="High" label="High">
            <Tag color="red">High</Tag>
          </Select.Option>
          <Select.Option value="Medium" label="Medium">
            <Tag color="blue">Medium</Tag>
          </Select.Option>
          <Select.Option value="Low" label="Low">
            <Tag color="gray">Low</Tag>
          </Select.Option>
        </Select>
      </Col>
    </Row>
  );
}
