const initState = {
  filter: {
    search: "",
    status: "All",
    prioriry: [],
  },
  todoList: [
    { id: 1, name: "Studying", completed: false, prioriry: "Medium" },
    { id: 2, name: "Play game", completed: true, prioriry: "Low" },
    { id: 3, name: "Watch movie", completed: false, prioriry: "Medium" },
  ],
};

const rootReducer = (state = initState, action) => {
    switch(action.type) {
        case 'todoList/addTodo':
            return {
                ...state,
                todoList: [
                    ...state.todoList,
                    { id: 5, name: "Sleep", completed: false, prioriry: "Low" },
                ]
            }
         default:
             return state;   
    }
};

export default rootReducer;
